package id.walt.vclib

import com.beust.klaxon.Klaxon
import com.nimbusds.jwt.SignedJWT
import id.walt.vclib.Helpers.toCredential
import id.walt.vclib.Helpers.toMap
import id.walt.vclib.model.VerifiableCredential
import io.kotest.assertions.json.shouldEqualJson
import io.kotest.core.spec.style.StringSpec
import io.kotest.matchers.shouldBe

class VCConversionTest : StringSpec({

    "1. jwt parser test" {
        val jwt = "eyJraWQiOiJkaWQ6ZWJzaToyMndGN2tVREZUM3RrdTVxdzQ0TGRWdThvMmRKc1U0RkxOSjd6SzRLbmhlbUdLeDUiLCJ0eXAiOiJKV1QiLCJhbGciOiJFUzI1NksifQ..gtdEFs35FUsRz1PYElN99C4Y9QCYejqN3wNVkpYwKC4U8ACnnaDVP5q1fX007qAtA-f0Dm39iA9ShtQIjn_zHA"
        val cred = jwt.toCredential()
        VerifiableCredential::class.java.isAssignableFrom(cred::class.java) shouldBe true
        cred.jwt shouldBe jwt
        cred.json?.shouldEqualJson(SignedJWT.parse(jwt).jwtClaimsSet.claims["vc"].toString())
    }

    "2. parse and convert vp" {
        val vp = "{\"@context\" : [\"https://www.w3.org/2018/credentials/v1\"], \"id\" : \"id\", \"holder\" : \"did:ebsi:00000004321\", \"verifiableCredential\" : [\"eyJraWQiOiJkaWQ6a2V5Ono2TWtvNDU2OTdrRGI4SnZERWR2QXpBZHM0R1FxUEphWEdEQzg1UkFUQUROdXZndyIsInR5cCI6IkpXVCIsImFsZyI6IkVkRFNBIn0..n2JSEDA3jAeRHGRareF2ScDoVDQ1K6upbvIgszitRNyYF319H8kuKVq9Dn0IHSVO30VqbWsuvyk0U5-JL1RECw\"], \"type\" : [\"VerifiablePresentation\"]}"
        Klaxon().toJsonString(vp.toCredential().toMap()) shouldEqualJson vp
    }
}) {}